package Chapter_6;

public class Review_TimeConverter {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int sum, num;
		sum = 0;
		for(num = 3; num <= 10; num ++)
		  {
		  	sum = sum + num;
		  	System.out.println(num);
		  }
		System.out.println(sum);
	}

}
